INSERT INTO `world`.`creature_text` VALUES 
('2284', '0', '0', 'Finally they show mercy!  They\'ve given us something to drink!', '12', '0', '100', '0', '0', '0', '0', 'Captured Farmer'),
('2284', '1', '0', 'And Shindigger Stout too!  Thank the Light!  I was parched!', '12', '0', '100', '0', '0', '0', '0', 'Captured Farmer'),
('2284', '2', '0', '%s raises his mug to his eye, and frowns.', '16', '0', '100', '0', '0', '0', '0', 'Captured Farmer'),
('2284', '3', '0', '%s swaggers heavily.', '16', '0', '100', '0', '0', '0', '0', 'Captured Farmer'),
('2284', '4', '0', '%s mumbles incoherent words.', '16', '0', '100', '1', '0', '0', '0', 'Captured Farmer');
